<script>
	import Pump from './Pump.svelte';

	let size = $state(15);
	let burst = $state(false);

	function reset() {
		size = 15;
		burst = false;
	}
</script>

<Pump
	inflate={() => {
		size += 5;
		if (size > 75) burst = true;
	}}
	deflate={() => {
		if (size > 0) size -= 5;
	}}
/>

{#if burst}
	<button onclick={reset}>new balloon</button>
	<span class="boom">ðŸ’¥</span>
{:else}
	<span class="balloon" style="scale: {0.01 * size}">
		ðŸŽˆ
	</span>
{/if}
